INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (1, 'Parkeercontrole','Gemeente Rotterdam','Stadsbeheer - Parkeervoorzieningen','De gemeente Rotterdam wil de stad graag leefbaar, bereikbaar en veilig houden. Het aantal auto’s in de stad beperken draagt daaraan bij. Dat doet de gemeente onder andere door gratis parkeren op P+R terreinen aan te moedigen en gebruik van het openbaar vervoer te stimuleren. Binnenin de stad is parkeren betaald en voert de gemeente parkeercontroles uit. Zo zorgen we ervoor dat de schaarse parkeerruimte vooral beschikbaar blijft voor bewoners en ondernemers.','Zelflerend','Veiligheid en handhaving','https://algoritmeregister.rotterdam.nl/p/Onzealgoritmes/53761720551735697','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (1,'De gemeente gebruikt algoritmen om:
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (1,'De gemeente Rotterdam wil de stad graag leefbaar, bereikbaar en veilig houden. Het aantal auto’s in de stad beperken draagt daaraan bij. Dat doet de gemeente onder andere door gratis parkeren op P+R terreinen aan te moedigen en gebruik van het openbaar vervoer te stimuleren. Binnenin de stad is parkeren betaald en voert de gemeente parkeercontroles uit. Zo zorgen we ervoor dat de schaarse parkeerruimte vooral beschikbaar blijft voor bewoners en ondernemers.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (1,'','Medewerkers van de gemeente Rotterdam controleren de data op feiten. Zij controleren het kenteken op de foto, het parkeerrecht, de parkeerlocatie en uitzonderingssituaties. De gemeente schrijft alleen een parkeerboete uit als alle feiten kloppen. Bij twijfel zal de medewerker niet handhaven. De rekenregels in het model zijn gebaseerd op bevindingen van experts. De berekening is geautomatiseerd. De uitkomsten van de berekening vormen een van de bronnen die gebruikt worden in het formuleren van de gebiedsaanpak. Een team van experts en beleidsmakers formuleert de uiteindelijke gebiedsaanpak op basis van data.','Het systeem heeft een laag risico. Het belangrijkste risico is dat het systeem een kenteken verkeerd herkent en dat iemand die het niet verdient een boete krijgt. Dit kan gebeuren als een letter of cijfer op het kenteken foutief wordt herkend door zowel het algoritme als de medewerker van gemeente Rotterdam. Om dit risico te beheersen, krijgen mensen de mogelijkheid om binnen 6 weken schriftelijk bezwaar te maken. Wie bezwaar maakt, krijgt de gelegenheid om de foto van het kenteken te zien en een situatiefoto, als die beschikbaar is. Eventuele omstanders, irrelevante kentekens en andere privacygevoelige informatie worden onherkenbaar gemaakt op deze foto’s. Regelmatige doorontwikkeling van het model op basis van nieuwe data en adviezen zorgt ervoor dat het proces steeds beter wordt.','',1);
