INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (2, 'Aardgasvrij','Gemeente Rotterdam','Stadsontwikkeling - Duurzaamheid','Nederland moet de komende jaren stoppen met het gebruik van aardgas voor het verwarmen van woningen, kantoren en ander vastgoed. Om gebouwen aardgasvrij te maken hebben we schone energie nodig, nu en in de toekomst. De belangrijkste schone energiebronnen in de Rotterdamse regio zijn; restwarmte van de industrie, warmte van geothermie, aquathermie, schone elektriciteit van zon en wind en duurzame gassen, waaronder waterstof. De beschikbaarheid én geschiktheid van deze energiebronnen voor bestaande gebouwen is in opdracht van de gemeente geanalyseerd: Regionale energie strategie 1.0, 2021 (pdf) <https://www.resrotterdamdenhaag.nl/wp-content/uploads/2021/04/RES-1.0-Regio-Rotterdam-Den-Haag.pdf>.','Rule based','Infrastructuur','https://algoritmeregister.rotterdam.nl/p/Onzealgoritmes/53761720551735605','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (2,'','','Het algoritme verwerkt geen informatie die kan leiden tot ongelijke behandeling. Het RAM-model verwerkt geen persoonsgegevens (zoals leeftijd, geslacht, inkomen, enzovoort). De informatie is op geen enkele wijze herleidbaar tot een natuurlijk persoon. Daarnaast passen we de gegevens niet toe in uitvoeringsprocessen waarbij impact zou kunnen zijn op individuen.','','',2);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (2,'Nederland moet de komende jaren stoppen met het gebruik van aardgas voor het verwarmen van woningen, kantoren en ander vastgoed. Om gebouwen aardgasvrij te maken hebben we schone energie nodig, nu en in de toekomst. De belangrijkste schone energiebronnen in de Rotterdamse regio zijn; restwarmte van de industrie, warmte van geothermie, aquathermie, schone elektriciteit van zon en wind en duurzame gassen, waaronder waterstof. De beschikbaarheid én geschiktheid van deze energiebronnen voor bestaande gebouwen is in opdracht van de gemeente geanalyseerd: Regionale energie strategie 1.0, 2021 (pdf) <https://www.resrotterdamdenhaag.nl/wp-content/uploads/2021/04/RES-1.0-Regio-Rotterdam-Den-Haag.pdf>.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (2,'','De rekenregels in het model zijn gebaseerd op de mening van experts. De berekening gebeurt geautomatiseerd. De uitkomsten van de berekeningen zijn een van de bronnen die gebruikt worden in het formuleren van de gebiedsaanpak. De uiteindelijke gebiedsaanpak wordt geformuleerd door een team van experts en beleidsmakers, op basis van gesprekken met bewoners, afspraken met externe partijen. Het model en de data zijn ondersteunend.','Dit algoritme is een eerste versie, gebaseerd op de gegevens die op dit moment beschikbaar zijn. De uitkomsten bieden een schatting van de totale kosten en worden gebruikt als indicatoren en niet als absolute waarheden. Intern en extern wordt dit duidelijk gecommuniceerd en er wordt het advies gegeven om het slechts als richtinggevend middel te gebruiken. Regelmatige doorontwikkeling van het model op basis van nieuwe data en adviezen zal ervoor zorgen dat de voorspellingen van het product betrouwbaarder worden.','',2);
