INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (3, 'SpotRotterdam','Gemeente Rotterdam','Stadsontwikkeling - Economie, Team Economische Ontwikkeling Stad','In Rotterdam kan het op veel plaatsen erg druk zijn. Veel mensen vinden het fijn om afstand te houden tot anderen om een coronabesmetting te vermijden. Daarom biedt de gemeente Rotterdam SpotRotterdam aan. Dat is een online druktemeter waarmee bewoners en bezoekers live kunnen zien hoe rustig of druk het is in winkelcentra, parken, markten en op stations.','Zelflerend','Veiligheid en handhaving','https://algoritmeregister.rotterdam.nl/p/Onzealgoritmes/53761720551735397','In gebruik');
