INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (3,'In Rotterdam kan het op veel plaatsen erg druk zijn. Veel mensen vinden het fijn om afstand te houden tot anderen om een coronabesmetting te vermijden. Daarom biedt de gemeente Rotterdam SpotRotterdam aan. Dat is een online druktemeter waarmee bewoners en bezoekers live kunnen zien hoe rustig of druk het is in winkelcentra, parken, markten en op stations.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (3,'','Resono controleert de databron met de tellingen. Resono houdt bijvoorbeeld in de gaten of er beschadigde data tussen zitten. Als dat het geval is, informeert Resono de gemeente tijdig om dit op te lossen. Verder controleert Resono het model dat voorspelt hoeveel personen zich in een gebied bevinden.Omdat veranderingen in straatnamen zelden zijn, komen er zelden veranderingen voor in de data uit de BAG en OpenStreetMap. Dat betekent dat er geen menselijk toezicht nodig is om vanuit SpotRotterdam deze databronnen in de gaten te houden. Als er iets verkeerd gaat met de techniek van SpotRotterdam, dan is het enige gevolg dat er verkeerde tellingen in de applicatie komen te staan. Er is gekozen om geen permanent toezicht in te richten op SpotRotterdam gezien de geringe consequenties.','Het kan voorkomen dat de drukte die SpotRotterdam weergeeft niet overeenkomt met de drukte in het gebied. Daarom informeert de gemeente de gebruikers van SpotRotterdam dat het hun eigen verantwoordelijkheid blijft om voldoende afstand te houden en drukke plekken te vermijden. Daarnaast kunnen gebruikers van de app hun eigen mening over de drukte invoeren. Deze gegevens kunnen helpen om na te gaan of de drukte die de app opgeeft klopt met het druktebeeld dat app-gebruikers op dat moment hebben. Ook zorgt de gemeente ervoor dat bedrijven en andere partijen de gemeente kunnen benaderen om vragen te stellen over het gebied waarin zij gevestigd zijn.','',3);
