INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (5, 'Wmo-voorspelmodel','Gemeente Den Haag','Onderwijs, Cultuur en Welzijn','Het Wmo-voorspelmodel geeft een voorspelling van het aantal unieke gebruikers en de kosten van de Wmo met een voorspelhorizon van vijf jaar. Voorspellingen worden gedaan op wijkniveau voor zowel de Wmo-totaal als voor deelproducten van de Wmo (Hulp bij het Huishouden, Ondersteuning thuis en Hulpmiddelen en Diensten).','Rule based','Sociale zekerheid','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (5,'Het algoritme is ontwikkeld omdat de gemeente meer inzicht wil hebben in het gebruik en de kosten van de Wmo de komende jaren. De Haagse bevolking groeit en vergrijst waardoor het gebruik van de Wmo ook naar verwachting gaat toenemen. De vraag is met hoeveel mensen en in welke Haagse wijken en voor welke Wmo-voorzieningen? Het voorspelmodel beantwoordt deze vragen.','Het voorspelmodel doet geen uitspraken over (kansen van) individuele burgers en er is gekozen voor modellen die uitlegbaar zijn. Dit betekent dat we als gemeente geen modellen gebruiken waarvan niet goed is vast te stellen hoe het model tot een bepaalde uitkomst is gekomen (zgn. Black Box modellen). Het Wmo-voorspelmodel vervangt op geen enkele manier de kennis en kunde van onze consulenten en uitvoering. Het instrument is voor hen niet bedoeld, maar vooral voor beleid en het maken en onderbouwen van beleidskeuzes voor de langere termijn. Juist door de kennis van het Wmo-voorspel kunnen we tijdig anticiperen en alle individuen beter blijven ondersteunen.','','Met de inzichten uit het Wmo-voorspelmodel wordt beleid en uitvoering ondersteund bij beantwoorden van tactische en strategische vragen. Dit zijn vragen en onderwerpen die op de langere termijn spelen (bijvoorbeeld 5 á 10 jaar). Het model is niet bedoeld voor het beantwoorden van operationele vragen (onderwerpen die op korte termijn spelen)  en er is geen sprake van geautomatiseerde besluiten.','',5);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (5,'Met een regressiemodel wordt de relatie tussen Wmo-gebruik en predictoren geschat. Daarbij wordt gezocht naar de kenmerken met de kleinste voorspelfout (vastgesteld aan de hand van de maten: RMSE, MAE en MAPE). De kenmerken die het best in staat zijn het gebruik van de Wmo te voorspellen worden gebruikt voor
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (5,'Indien het noodzakelijk is een nieuw model te bouwen/te trainen, wordt de privacy officer betrokken bij de keuze van features en zal bij een geheel nieuw model ook opnieuw naar het ethisch kader worden gekeken.','Er is geen sprake van een geautomatiseerd systeem. De inzichten uit het voorspelmodel kunnen alleen door tussenkomst van mensen en met de contextkennis en ervaring van betrokken medewerkers worden gebruikt.','De risico''s van het algoritme zijn vooraf en tijdens het bouwen van het voorspelmodel in kaart gebracht. Omdat het voorspelmodel geen uitspraken doet over individuele personen maar over het gebruik van voorzieningen in wijken, was er geen risico dat met de uitkomsten uit het model de privacy van specifieke individuele personen zou kunnen worden geschonden. Verder is alleen gewerkt met wijken die voldoende groot zijn (meer dan 100 inwoners) en waar voldoende mensen gebruikmaakten van de Wmo (meer dan 100 gebruikers). Een reden hiervoor is dat hierdoor geen onthulling mogelijk is. Dat wil zeggen dat we niet door het combineren van kenmerken kunnen vaststellen wie de mogelijke gebruikers van Wmo-voorzieningen zijn. Verder is bij het bouwen van het model samen met de privacy officer afgewogen welke variabelen wel en welke variabelen niet vanuit privacy overwegingen meegenomen konden worden. Hierbij vormde de AVG steeds het uitgangspunt. Daarnaast is uitlegbaarheid van het model en de uitkomsten een relevant en doorslaggevend criterium. Tenslotte is gebruikgemaakt van De Ethische Data Assistent van de Universiteit van Utrecht om mogelijke ethische problemen vooraf in kaart te brengen. Dit heeft geleid tot het opstellen van een ethisch kader. De vraag welke rol bias speelt in het gebruik van het algoritme is een veel te algemene vraag, aangezien er vele vormen van bias zijn. Als het gaat om vooringenomenheid gericht op specifieke bevolkingsgroepen, dan speelt dat bij dit model geen rol. We zijn niet op zoek naar specifieke individuele personen met specifieke kenmerken, maar willen een inschatting maken van het gebruik van de Wmo in de hele wijk en voor heel Den Haag.','Het model wordt jaarlijks geactualiseerd. De voorspellingen van het oude model worden vergeleken met voorspellingen van het nieuwe model en vergeleken met de realisaties.',5);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (5,'','Er is geen wettelijke grondslag voor het voorspelmodel, maar de gemeente is verantwoordelijk voor de uitvoering van de Wmo en door het model kan de gemeente beter anticiperen op verwachte ontwikkelingen en burgers beter ondersteunen.',True,'Het Wmo-voorspelmodel maakt geen gebruik van persoonskenmerken.','Gezien de aard van het model is dit niet geregeld.',5);
