INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (6, 'Scanauto kentekenherkenning','Gemeente Den Haag','Stadsbeheer','Het doel van het algoritme is om kentekens automatisch te scannen ten behoeve van parkeerhandhaving en handhaving milieuzones.','Rule based','Veiligheid en handhaving','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (6,'Het doel van het algoritme is de herkenning van kentekens van voertuigen die in overtreding zijn voor respectievelijk naheffing parkeerbelasting en Boete Milieuzone (Wet Mulder).','Een burger komt in aanraking met de uitkomst van het algoritme bij een  overtreding - Uitgebreide informatiepagina op www.denhaag.nl','Nee, dit is in een aanvulling op de handmatige handhaving.','Met het algoritme wordt het proces ondersteund om te bepalen of een auto fout geparkeerd is (Fiscale Parkeerhandhaving) en  om te bepalen of een auto/vrachtauto ten onrechte in een bepaald gebied rijdt (Handhaving Milieuzones).','https://www.denhaag.nl/nl/parkeren.htm',6);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (6,'Een camera scant kentekens. Het algoritme combineert data van de RDW/ vergunningen, GBA en Nationaal Parkeer register op basis van vooraf gestelde regels (parameters) om te bepalen of er sprake is van fout parkeren of onterecht rijden in een Milieuzone. Algoritme berust bij de leveranciers. Gemeente neemt dienstverlening af en heeft geen directe bemoeienis bij of inhoudelijke kennis van de werking van de algoritmes.','','',True,'RDW, Vergunningen/ontheffingen, GBA, Nationaal Parkeer Register','6');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (6,'Door tussenkomst van een BOA (wel/geen naheffing/boete of. Wel/niet ontvankelijk / gegrond bezwaar). Dit proces en informatiestroom lopen via de dienstdoende BOA (opsporingsambtenaar).','Door tussenkomst van een BOA (wel/geen naheffing/boete of. Wel/niet ontvankelijk / gegrond bezwaar). Dit proces en informatiestroom lopen via de dienstdoende BOA (opsporingsambtenaar).','Inkoop van dienstverlening van leveranciers die ISO gecertificeerd zijn. Algoritmes blijven berusten bij de leveranciers. Er is sprake van een verwerkersovereenkomst en een DPIA om de risico''s voor de gemeente en de burgers zo klein mogelijk te houden.','Er zijn geen specifieke prestaties van het algoritme afgesproken. Dienstverlening door de leverancier wordt periodiek besproken.',6);
