INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (7, 'Voortijdig Schoolverlaters','Gemeente Den Haag','Onderwijs, Cultuur en Welzijn','Het VSV-voorspelmodel berekent aan de hand van verzuimmeldingen van Haagse leerlingen de kans dat een leerling (van 12 tot 18 jaar) ergens in het lopende schooljaar langdurig relatief gaat verzuimen. Langdurig relatief verzuim (LRV) wil in dit kader zeggen dat een leerling op een school voor voortgezet onderwijs (inclusief speciaal onderwijs) of mbo staat ingeschreven en dat hij of zij 28 dagen of langer ongeoorloofd afwezig is geweest van school. Het model voorspelt niet wanneer dit langdurig mogelijk precies gaat plaatsvinden.','Zelflerend','Bevolkingsonderzoek','','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (7,'Het algoritme had als doel om uit het grote aantal verzuimmeldingen dat de gemeente jaarlijks ontvangt (ongeveer 8000 per jaar) snel de verzuimmeldingen te vinden  van leerlingen met een groter risico op langdurig relatief verzuim. Het algoritme functioneert goed als het in staat is om de meeste leerlingen met dit hoge risico aan te wijzen en zo min mogelijk leerlingen die niet tot de doelgroep behoren. Het algoritme is niet bedoeld om zonder tussenkomst van mensen, leerlingen in te delen in risicogroepen en alleen op basis hiervan te handelen.','Het algoritme is niet in productie genomen. Indien hiertoe wel was besloten, dan zou dit merkbaar zijn geweest voor verzuimende leerlingen met een groter risico op langdurig verzuim. Zij zouden een intensievere begeleiding hebben gekregen van leerplichtambtenaren met als doel hen zo snel mogelijk terug te laten keren naar school. Een plan om alle betrokkenen te informeren was in de maak maar is door het beëindigen van het project niet voltooid.','','Het was de bedoeling dat het voorspelmodel leerplichtambtenaren zou ondersteunen bij het snel signaleren van verzuimende leerlingen met een hoog risico op langdurig relatief verzuim. De keuze aan welke leerling de leerplichtambtenaar extra ondersteuning zou gaan bieden, zou niet worden bepaald door het algoritme, maar door de leerplichtambtenaar zelf.','',7);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (7,'Het model berekende de kans dat een leerling gegeven zijn of haar verzuimmeldingen en achtergrondkenmerken langdurig zou kunnen gaan verzuimen. Hiervoor is een Gradient Boosted Decision Trees analyse uitgevoerd.','','',True,'De Leerling Basis Administratie vormde het hoofdbestand. Verder is dit bestand aangevuld met open data van het CBS.','7');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (7,'Aangezien het model niet in bedrijf is genomen, worden eventuele risico''s niet gemonitord.','Ja, het was de bedoeling dat het model de leerplicht ambtenaar ondersteunt bij zijn of haar werk, niet dat het model geautomatiseerde beslissingen zou nemen.','De risico''s van het algoritme zijn vooraf en tijdens het bouwen van het voorspelmodel in kaart gebracht. Het ging hierbij om het risico op mogelijke profilering op alleen de achtergrondkenmerken van een leerling en het mogelijk stigmatiserende effect dat uit zou kunnen gaan van het identificeren van leerlingen met een hoger risico op langdurig verzuim. Bij het bouwen van het model is samen met de privacy officer afgewogen welke variabelen wel en welke variabelen niet vanuit privacy overwegingen meegenomen konden worden. Hierbij vormde de AVG steeds het uitgangspunt. Ook was het relevant dat kenmerken van de verzuimmelding werden meegenomen in het model, zodat het risico vooral bepaald werd door bepaald verzuimgedrag. Verder waren uitlegbaarheid van het model en de kenmerken in het model een relevant en doorslaggevend criterium. Ten slotte is gebruik gemaakt van De Ethische Data Assistent van de Universiteit van Utrecht om mogelijke ethische problemen vooraf in kaart te brengen. Dit heeft geleid tot het opstellen van een ethisch kader. Omdat het model niet verder in productie is genomen, zijn er geen vervolgstappen gezet in relatie tot het toezicht houden op de risico''s en verder onderzoek naar mogelijke vooringenomenheid.','Er was afgesproken om de prestaties te beoordelen aan de hand van de ROC en AUC. De AUC is een maat die aangeeft hoe goed een model in staat is om onderscheid te maken tussen mogelijk langdurige verzuimende leerlingen en leerlingen met verzuimmeldingen die waarschijnlijk kortdurend verzuimen. Aangezien het model niet in bedrijf is genomen, worden deze maten niet gemonitord.',7);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (7,'','De gemeente is verantwoordelijk voor de uitvoering van de leerplichtwet. Het voorspelmodel zou leerplichtambtenaren hierbij ondersteunen.',True,'De verwerkingsverantwoordelijke was de directeur (Leerlingzaken). De rechtmatige grondslag werd gevormd door de leerplichtwet. De gemeente is verantwoordelijk voor de uitvoering van deze wet.','Nee, want dat is gezien het feit dat het model niet in bedrijf is genomen niet langer van toepassing.',7);
INSERT INTO metadata (id, schema, uuid, url, contact_email, area, lang, revision_date, algoritme_id) VALUES (7,'Algoritmeregister 0.2','98bd3bd4-d5f9-42df-a9de-513dfd502969','https://data.overheid.nl/dataset/algoritme-register','datashop@denhaag.nl','Den Haag','Nederlands', '44484',7);
INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (8, 'Zicht op ondermijning','Gemeente Den Haag','Bestuursdienst','Er worden meerdere analyses uitgevoerd in het project Zicht op Ondermijning, zie ook website www.zichtopondermijning.nl. Er wordt continu doorgewerkt aan het toevoegen van inzichten en (nieuwe) analyses.','Rule based','Veiligheid','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (8,'In de analyses die voor het dashboard Zicht op Ondermijning worden uitgevoerd, worden regelmatig algoritmen toegepast. Soms zijn dit heel eenvoudige algoritmen. Waar rekenregels worden toegepast, is in strikte zin immers al sprake van een algoritme. Er worden ook methodologisch ingewikkeldere algoritmen gebruikt, zoals een beslisboomalgoritme bij de analyse naar jonge aanwas en subgroup discovery bij de groepsanalyse naar betrokkenen bij verdachte transacties. Voor het dashboard Zicht op Ondermijning worden algoritmen nooit toegepast om op het niveau van individuele adressen, bedrijven, huishoudens of personen inzichten te bieden of conclusies te trekken. Individuele gegevens zijn gepseudonimiseerd en uitkomsten zijn altijd geaggregeerd. Iedere analyse op het dashboard wordt in het onderdeel Verdiepende informatie in detail beschreven. Daar staat uitgelegd welke analysemethoden worden toegepast, of dit algoritmen zijn, welke keuzes daarbij gemaakt zijn en welke kenmerken van adressen, bedrijven, huishoudens of personen zijn beschouwd. Migratieachtergrond wordt in dit dashboard alleen gebruikt als kenmerk in beschrijvende statistieken van groepen huishoudens of personen, mits daar voldoende onderbouwing voor bestaat. Het wordt niet gebruikt als kenmerk in classificeringsalgoritmen die worden toegepast op huishoudens of personen.','Er is voor burgers geen dagelijks effect. De analyses uit de City Deal zijn bedoeld om trends en patronen van ondermijnende criminaliteit in kaart te brengen, deze worden niet in individuele gevallen benut. De opbrengsten van analyses worden publiekelijk gedeeld via een dashboard. De gegevens zijn veelal historisch (hebben betrekking op eerdere jaren) en zijn niet real time','De beoogde resultaten en inzichten zijn enkel en alleen te bereiken door te werken met microdata van het CBS binnen de kaders van de CBS-wet. Om die reden is daar in 2017 de City Deal ''Zicht op Ondermijning'' voor opgesteld en ondertekend. Zie ook: https://zoek.officielebekendmakingen.nl/stcrt-2017-48699.html','De uitkomsten van de analyses (en dus algoritmes) worden niet direct gebruikt voor beslissingen in individuele gevallen, hoogstens als input voor beleidskeuzes op hoger abstractieniveau.','www.zichtopondermijning.nl',8);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (8,'','De algoritmes en/of codes zijn niet gepubliceerd.','',True,'Omdat er met meerdere algoritmes wordt gewerkt is dit lastig om in een veld op te sommen.','8');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (8,'Alle onderzoeksvragen worden voorgelegd aan de ethische commissie van het CBS voordat een onderzoeksvraag door een analist in behandeling wordt genomen.','Ja, voordat een analyse-uitkomst openbaar wordt gemaakt, controleert het CBS het inzicht op onthullingsrisico.','Het CBS hoeft in belangrijke mate toezicht op het gehele project en waakt o.a. voor onthullingsrisico''s.','N.v.t., gezien de rol/benutting van algoritmen binnen het project Zicht op Ondermijning.',8);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (8,'','https://zoek.officielebekendmakingen.nl/stcrt-2017-48699.html - Verwerkingen voor wetenschappelijk en historisch onderzoek, statistische doeleinden en archivering in het algemeen belang worden altijd verenigbaar geacht met het oorspronkelijke verzameldoel. Maar ook als het geen verdere verwerkingen zijn, mogen persoonsgegevens worden verwerkt voor deze doeleinden, mits aan de vereisten in de Verordening wordt voldaan. De Verordening eist dat voor de bescherming van de rechten en vrijheden van betrokkenen passende waarborgen worden getroffen. Die waarborgen moeten er onder andere voor zorgen dat technische en organisatorische maatregelen zijn getroffen om zo min mogelijk persoonsgegevens te verwerken, zoals pseudonimiseren of anonimiseren.',True,'De verwerking van persoonsgegevens ten behoeve van de uit te voeren analyses geschiedt met inachtneming van de Wet bescherming persoonsgegevens (Wbp) en met ingang van 25 mei 2018 de Algemene verordening gegevensbescherming (AVG) en de Uitvoeringswet Algemene verordening gegevensbescherming alsmede, afhankelijk van de aard van de te verwerken persoonsgegevens, andere relevante wettelijke bepalingen met betrekking tot de bescherming van persoonsgegevens, zoals de Wet politiegegevens (Wpg), de Wet justitiële en strafvorderlijke gegevens (Wjsg) en de Wet op het Centraal bureau voor de statistiek (Wet CBS).','n.v.t.',8);
INSERT INTO metadata (id, schema, uuid, url, contact_email, area, lang, revision_date, algoritme_id) VALUES (8,'Algoritmeregister 0.2','44cae28c-37a7-4385-bc5b-12208b0491ab','https://data.overheid.nl/dataset/algoritme-register','datashop@denhaag.nl','Den Haag','Nederlands', '44467',8);
INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (9, 'Regionaal verkeersmodel: V-MRDH','Gemeente Den Haag','Stedelijke Ontwikkeling','Het V-MRDH is een verkeersmodel in de software Omnitrans (ontwikkelaar is DAT-Mobility, Deventer). Het algoritme berekent de hoeveelheid verplaatsingen (auto, OV en fiets) op basis van inputgegevens zoals netwerken en demografische data. Het model wordt gebruikt, intern bij Den Haag, bij andere gemeenten en bij verkeerskundige adviesbureaus.','Rule based','Infrastructuur','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (9,'De hoeveelheid verkeer op de weg ten gevolge van een plan wordt berekend. Het plan kan zijn: een nieuwe weg of de bouw van een aantal woningen. Het model berekent de hoeveelheid verkeer op hoofdlijnen., bedoeld om scenario''s (varianten) met elkaar te vergelijken.','De burger krijgt nooit te maken met een directe uitkomst van een berekening met het verkeersmodel.','Voor de hele MRDH is er een verkeersmodel om toekomstige situaties door te rekenen. Wanneer in een studie ook rijkswegen zijn betrokken, wordt vaak samengewerkt met RWS omdat RWS modellen heeft voor het verkeer op rijkswegen.','Een berekening wordt gebruikt om beleid op het gebied van mobiliteit te maken of te onderbouwen (mobiliteit is hier de verplaatsing van mensen en goederen). Het algoritme levert nooit direct een besluit op.','Verkeersmodel | MRDH Metropoolregio Rotterdam Den Haag.',9);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (9,'Den Haag is opgedeeld in 1.000 zones met elk gemiddeld ongeveer 500 inwoners. Die inwoners maken gemiddeld ongeveer 4 verplaatsingen per dag, verdeeld over de motieven werk, zakelijk, onderwijs, winkel en sociaal-recreatief. Het model schat het totaal aantal verplaatsingen en de verdeling over auto, OV en fiets. De bestemming is gebaseerd op de afstand die mensen gemiddeld bereid zijn af te leggen. De relatieve aantrekkelijkheid van auto, OV en fiets bepaalt de verhouding tussen deze drie modaliteiten. Het model is van het zwaartekrachtprincipe: een dichterbij gelegen bestemming heeft een grotere aantrekkingskracht dan een bestemming ver weg.','Onbekend','',True,'Aantallen inwoners en arbeidsplaatsen van het CBS. Plannen van alle betrokken wegbeheerders, rijk, gemeenten en provincie. Ook bestaande en toekomstige netwerken zijn input voor de berekeningen.','9');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (9,'Resultaten van het model worden altijd geanalyseerd door specialisten.','Resultaten van het model worden altijd geanalyseerd door specialisten.','Resultaten van het model worden altijd geanalyseerd door specialisten.','Geen afspraken en geen monitoring van toepassing.',9);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (9,'','Input is noodzakelijk voor gemeentelijk beleid.',True,'Er zijn geen persoonsgegevens.','Daar is nooit sprake van. Binnen projecten kunnen mensen discussiëren over de uitkomsten van het model. De uitkomst van het model wordt gebruikt om een advies te onderbouwen.',9);
INSERT INTO metadata (id, schema, uuid, url, contact_email, area, lang, revision_date, algoritme_id) VALUES (9,'Algoritmeregister 0.2','d5718cc3-f71e-4466-96ba-209957fb7043','https://data.overheid.nl/dataset/algoritme-register','datashop@denhaag.nl','Den Haag','Nederlands', '44480',9);
INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (10, 'Verkeersmodel bereikbaarheidsregie','Gemeente Den Haag','Stadsbeheer','Het is een verkeersmodel om stadsbreed de hinder van activiteiten (bouwwerkzaamheden, wegwerkzaamheden en evenementen) op het netwerk Bereikbaarheidsregie (het Haags hoofd verkeersnetwerk) inzichtelijk te maken, te objectiveren en zo te ondersteunen bij de afstemming en planning van die activiteiten zodat de Haagse Bereikbaarheid zo veel mogelijk geborgd blijft.','Rule based','Infrastructuur','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (10,'Het doel van het  model is om de impact op de verkeersdoorstroming van een enkele activiteit of de samenhang tussen meerdere activiteiten te zien in een gemodelleerde en geabstraheerde omgeving.','Een burger krijgt niet direct met het algoritme te maken. Als een burger gebruikmaakt van de openbare ruimte als deelnemer aan het verkeer en er zijn omleidingen dan merkt de burger dat het algoritme is gebruikt om te komen tot een zo optimaal mogelijke omleiding met zo min mogelijk overlast.','Ja, maar na een marktconsultatie bleek er niets te bestaan dat aan de vraag voldeed. Dit model is nieuw voor de gemeente Den Haag ontwikkeld door de markt.','De output van het model wordt gebruikt ter onderbouwing van het afgeven van een positief besluit voor een bouwwerk, wegwerk of evenement met impact op het Haagse hoofd verkeersnetwerk in het Stedelijke BereikbaarheidsOverleg (SBO). Daarmee vormt het input voor het instemmingsbesluit.','https://www.dat.nl/',10);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (10,'Dit is een intern gebruikt model t.b.v. input voor gesprekken met de initiatiefnemers van bouwwerkzaamheden, wegwerkzaamheden en evenementen om maatregelen te nemen om ook de verkeersdoorstroming voldoende te laten functioneren. Het is een objectieve berekening ter ondersteuning van de verkeerskundige toets op mogelijke knelpunten die ontstaan door activiteiten in de stad (bouwwerkzaamheden wegwerkzaamheden en evenementen). Dit model geeft een inschatting waar gegeven deze activiteiten en verkeersstromen het mogelijk gaat knellen. Dit geeft input voor de afstemming met de initiatiefnemers van de activiteiten, zodat de (omleidings)routes voldoende blijven functioneren. De output (hinder) wordt gekwalificeerd in termen van aantal gehinderden, reistijdvertraging, wachttijden, etc.','','',True,'Er is initieel gebruikgemaakt van het V-MRDH model (HB-matrix, routekeuzes en intensiteiten) aangevuld met anonieme data uit de koplussen (nullen en enen) van 50 geregelde kruispunten.','10');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (10,'n.v.t.','Het model wordt door een medewerker gevoed met data waarna het kan gaan rekenen. Het model wordt gebruikt als analyse voor de duiding van verkeersstromen door activiteiten. De output wordt gewogen in relatie tot de gesprekken met initiatiefnemers.','Er zijn geen specifieke risico''s in de zin van privacyaspecten rondom persoonsgegevens. Bias speelt geen rol.','Er zijn geen specifieke afspraken gemaakt om het algoritme te toetsen. Er is een beheer- en onderhoudscontract met de leverancier afgesproken.',10);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (10,'','n.v.t.',True,'Er is geen sprake van verwerking van persoonsgegevens.','n.v.t.',10);
INSERT INTO metadata (id, schema, uuid, url, contact_email, area, lang, revision_date, algoritme_id) VALUES (10,'Algoritmeregister 0.2','2b9a46bb-7ba1-4c12-9be4-20d14193dab5','https://data.overheid.nl/dataset/algoritme-register','datashop@denhaag.nl','Den Haag','Nederlands', '44486',10);
INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (11, 'Dynamische microsimulatiemodellen','Gemeente Den Haag','Stadsbeheer','Het programma geeft een visuele weergave van de verkeersafwikkeling op wegen en kruispunten. De werkelijkheid en mogelijke toekomstige werkelijkheid worden geschetst/gesimuleerd op basis van de input. Met dit computermodel kunnen opties voor weg- en kruispuntsinrichtingen en verkeersregelingen vergeleken worden en helpt het dus bij advies en keuzes.','Rule based','Infrastructuur','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (11,'Het is bedoeld als hulpmiddel voor de wegbeheerderstaken om de werkzaamheden adequaat te kunnen uitvoeren, eventueel verbeteren en keuzes te onderbouwen.','De burger merkt niet direct iets van het programma. Uiteindelijk merkt de burger als verkeersdeelnemer iets van de keuzes die de gemeente als wegbeheerder, mede op basis van uitkomsten van simulaties, heeft gemaakt voor de verkeersafwikkeling/ een goede doorstroming.','Dit is het meest gebruikte en beproefde microsimulatiepakket voor verkeersafwikkeling. Er is geen aanleiding om iets anders te gebruiken.','Het programma wordt gebruikt als hulpmiddel bij (bestuurlijke) besluitvorming over voorgenomen herinrichting van wegen en/of kruispunten en analyse van de werking van verkeersregelingen voor kruispunten. Er wordt dan gekeken wat de herinrichting en/of het regelprogramma voor consequentie heeft  in termen van verkeersafwikkeling.','www.ptvgroup.com',11);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (11,'Het programma geeft een visuele weergave van de verwachte verkeersstromen gegeven de beoogde inrichting van de weg en de beoogde instellingen van de verkeerslichten en de te verwachten verkeersstromen.','www.ptvgroup.com','',True,'Er wordt gebruikgemaakt van anonieme verkeersdata (aantallen voertuigen, fietsers etc. per uur/etmaal), de indeling van de weg, instellingen van het verkeersregelprogramma.','11');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (11,'Via de gebruikersgroepen genoemd in de vorige regel.','De uitkomsten zijn input voor verdere verkeerskundige analyse.','Er zijn geen specifieke risico''s in de zin van privacyaspecten rondom persoonsgegevens. Bias speelt geen rol.','Er zijn gebruikersgroepen van wegbeheerders waarmee periodiek wordt afgestemd door de leverancier om eventuele verbeteringen door te voeren.',11);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (11,'','N.v.t. De wettelijke basis ligt in een goede uitvoering van de wegenverkeerswet. Het is een hulpprogramma voor het werk van de wegbeheerder.',True,'Er is geen sprake van persoonsgegevens, alleen verkeersgegevens, weggegevens en verkeerslichtgegevens.','nee',11);
INSERT INTO metadata (id, schema, uuid, url, contact_email, area, lang, revision_date, algoritme_id) VALUES (11,'Algoritmeregister 0.2','226a697f-6bbf-4514-82f7-4a3ab1945518','https://data.overheid.nl/dataset/algoritme-register','datashop@denhaag.nl','Den Haag','Nederlands', '44486',11);
INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (12, 'Microsimulatiemodellen geregelde kruispunten','Gemeente Den Haag','Stadsbeheer','COCON is het acroniem voor "COherent CONglomeraat van verkeersregeltechnische software" en is in de verkeersregeltechnische wereld binnen Nederland het meest gebruikte programma om de volgorde waarin verkeersstromen op kruisingen worden afgewikkeld (en wat wel en niet tegelijk mag) te bepalen. De uitkomst is input voor het voor elk kruispunt te maken verkeersregelprogramma.','Rule based','Infrastructuur','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (12,'Het ontwerpen van fasevolgordes van verkeerslichten.','Als een burger gebruikmaakt van de openbare ruimte als deelnemer aan het verkeer komt men indirect in aanraking met de uitkomsten van COCON-berekeningen voor kruispunten. De burger merkt dit aan de volgordeverdeling en tegelijk of juist niet tegelijk mogen kruisen in de groenfases van verkeerslichten.','Er zijn geen alternatieven overwogen. Binnen de verkeerskundige wereld is dit product onomstreden.','Er is niet echt sprake van de ondersteuning van een besluitvormingsproces, maar een primaire overheidstaak in relatie tot de verkeersveiligheid namelijk het ontwerp van (veilige) en optimaal functionerende kruispunten.','https://dtvconsultants.nl/producten-en-diensten/cocon-en-graphium/',12);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (12,'Met COCON worden de zogenaamde fasevolgordes op kruispunten met verkeerslichten bepaald. COCON ondersteunt en vergemakkelijkt de werkzaamheden voor verkeersdeskundigen. De software verricht ook (ontwerp) berekeningen voor variantenonderzoek. Bij het wijzigen van bepaalde uitgangspunten van een regeling, wordt duidelijk welke effecten dat oplevert. Met COCON kunnen verkeerskundigen ook elkaar adviseren: hoe kan een met verkeerslichten geregeld kruispunt het beste worden ingericht, rekening houdend met de indeling van de rijstroken, de opstellengten, verdeling van de verkeersbewegingen, enzovoort.','zie website https://dtvconsultants.nl/producten-en-diensten/cocon-en-graphium/','',True,'Er wordt gebruikgemaakt van anonieme data over verkeersintensiteiten in relatie tot hoe een met verkeerslichten geregeld kruispunt het beste kan worden ingericht, rekening houdend met de indeling van de rijstroken, de opstellengten, verdeling van de verkeersbewegingen.','12');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (12,'n.v.t.','Ja','Er zijn geen specifieke  risico''s in de zin van privacyaspecten rondom persoonsgegevens. Bias speelt geen rol.','Leverancier heeft een gebruikersgroep wegbeheerders en aan de hand van periodieke afstemming voeren ze verbeteringen door.',12);
