INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (13, 'Automatische regelsoftware verkeerslichten','Gemeente Den Haag','Stadsbeheer','TLCGen is een applicatie voor het maken/genereren van uniforme, hoogwaardige en beheerbare verkeersregelprogramma''s voor verkeerslichten.','Rule based','Infrastructuur','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (13,'Het ontwerpen van regelprogramma''s voor in verkeerslichten. Dit wordt ontworpen met TLCGen. Het functioneert goed als TLCGen het goede regelprogramma genereert dat  voldoet aan de wetten en regels over verkeerslichten. En als het regelprogramma goed afgesteld is voor die specifieke kruising in termen van veilig en vlot.','Als een burger gebruikmaakt van de openbare ruimte als deelnemer aan het verkeer en dan in aanraking komt met het regelprogramma dat met TLCGen gemaakt is. De burger merkt dit aan de optimale afstelling van de groenfases van verkeerslichten.','Nee, er zijn geen serieuze alternatieven. Er zijn andere generatoren van verkeersregelprogramma''s beschikbaar echter deze leveren minder kwaliteit, meer handwerk op dan TLCGen en dus een groter risico voor de wegbeheerder en een lagere beheerbaarheid.','Er is niet echt sprake van de ondersteuning van een besluitvormingsproces. Het is een primaire overheidstaak van de gemeente als wegbeheerder om het verkeer veilig te ordenen en vlot te geleiden.','https://www.codingconnected.eu/software/tlcgen/',13);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (13,'TLCGen is een applicatie voor het maken/genereren van verkeersregelprogramma''s voor verkeerslichten. Daarmee wordt dus voor een kruispunt een op die plek geënt regelprogramma gemaakt dat zorgt voor vellige en vlotte verkeersafwikkeling. De input wordt gevormd door de fysieke eigenschappen van het kruispunt en de volgordeverdeling van verkeersstromen, in combinatie met wetten, regels en verkeersbeleidsuitgangspunten.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (13,'n.v.t.','Menselijke tussenkomst is puur gericht om de input te leveren voor het regelprogramma.','Er zijn geen specifieke risico''s in de zin van privacyaspecten rondom persoonsgegevens. Bias speelt geen rol.','TLCGen is gecertificeerd door de Statistic Committee van Talking Traffic (landelijk samenwerkingsverband). Daarmee voldoen we aan de hoogste standaarden.',13);
