INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (14, 'Voorspellen en voorkomen verkeersongevallen','Gemeente Den Haag','Stedelijke Ontwikkeling','Voorspelmodel dat op basis van een brede dataset met wegkenmerken en ongevallen risicoscores geeft op verkeersongevallen.','Zelflerend','Veiligheid en handhaving','','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (14,'Het doel van het model is tweeledig. In de eerste plaats het ontwikkelen van effectief verkeersveiligheidsbeleid. Daarnaast kan het model worden ingezet om in concrete projecten twee of meer ontwerpen met elkaar te vergelijken op de score van verkeersveiligheid.','Specifiek voor verkeerskundigen, burgers zien uiteindelijk aanpassingen aan de weg, minder ongevallen (is doel).','Destijds was dit het enige model dat correlaties onderzoekt tussen ongevalcijfers en kenmerken van de weg, het kruispunt of de omgeving.','Het model is een hulpmiddel voor het prioriteren van verkeersveiligheidsbeleid.  Er is dus geen sprake van geautomatiseerde besluitvorming.','www.ongevalrisico.nl
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (14,'1 set met wegen waarop wel ongevallen zijn gebeurd en 0 set met wegen waarop geen ongevallen zijn gebeurd. Algoritme gebruikt ''aantal ongevallen'' als target variabele (classificatie) en probeert verschillen te zoeken tussen eigenschappen 1 en 0 sets.','','',True,'www.ongevalrisico.nl
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (14,'Door de expert.','Ja, adviserende uitkomsten voor wegexperts. Modeluitkomsten worden nooit 1 op 1 overgenomen.','Er wordt uitdrukkelijk geen data gekoppeld die is te herleiden naar persoonlijk niveau. De uitkomsten worden door een expert beoordeeld voor gebruik.','Hier zijn geen afspraken voor gemaakt.',14);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (14,'','Noodzakelijk voor gemeentelijk beleid.',True,'Er is geen sprake van werken met persoonsgegevens.','Het algoritme zoekt een verband tussen objectief vast te stellen variabelen. Het gaat om de correlatie van geregistreerde ongevallen en objectief waarneembare data van de weg of de omgeving. De gebruiker besluit hier wel of geen consequentie aan te verbinden. Daarom is er geen sprake van een mogelijkheid om hier bezwaar tegen te maken.',14);
