INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (15, 'Chatbot Harry: reis en routeadvies','Gemeente Den Haag','Stedelijke Ontwikkeling en Stadsbeheer','Een zelflerende chatbot die bezoekers van Scheveningen actuele en adequate route- en reisadviezen kan geven op basis van open data en data van de gemeente. Uitgevoerd in het kader van Startup in Residence. Ontwikkeling is begin 2021 stopgezet. Er was onvoldoende budget beschikbaar.','Zelflerend','Toerisme','','Buiten gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (15,'Het doel van Chatbot harry was om bezoekers een alternatief voor de auto te geven voor hun strandbezoek.','Door actuele informatie of door voorspelde informatie obv historische data kunnen reizigers een andere keuze maken dan met de auto te komen.','Ja, deze bleken echter niet te voldoen.','Chatbot Harry was bedoeld als dienstverlening aan de burger niet om een besluitvormingsproces in de organisatie te ondersteunen.','',15);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (15,'De gemeente heeft via het Startup in Residence programma samen met de startup Avenieuw gezocht naar een manier om de bezoekers van de stad zo makkelijk en gericht als mogelijk van de juiste en meest actuele informatie te voorzien. Daarvoor is een chatbot ontwikkeld, die 24/7 op basis van vele open databronnen, maar ook databronnen van private partijen en van de gemeentelijke diensten tijdens het gesprek met de bezoeker de juiste antwoorden kan geven over zijn of haar bestemming en de reis er naar toe.','','',True,'Open data als reistijden, bezetting van parkeergarages en OV-informatie.','15');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (15,'Dit zou via een verwerkingsovereenkomst en periodieke monitoring moeten worden geregeld. Echter dit stadium is nooit bereikt.','Nee','Het algoritme is niet in productie genomen en er is daarom ook geen DPIA uitgevoerd (privacy impact analyse). Er is wel nagedacht over het risico dat persoonsgegevens worden achtergelaten in de chat.','Aantal unieke gebruikers van de chatbot.',15);
