INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (18, 'Bevolkingsprognosemodel','Gemeente Den Haag','Stedelijke Ontwikkeling','De bevolkingsprognose geeft Inzicht in de samenstelling, spreiding en de toekomstige omvang van de bevolking.','Rule based','Bevolkingsonderzoek','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (18,'Inzicht te hebben in de (mogelijke) toekomstige ontwikkeling van de Haagse bevolking naar leeftijd.','Het model heeft geen directe invloed op het dagelijks leven van burgers.','GBPRO is een model dat gemaakt is door en voor Nederlandse gemeenten. Er zijn geen vergelijkbare alternatieven beschikbaar waarbij je op lokaal (gemeente) niveau de situatie qua sterfte, geboorte en migratie zo goed mogelijk kan inschatten.','De uitkomsten zijn input voor gemeentelijke beleidsmakers zoals bv op het gebied van ruimtelijke ordening, stedelijke ontwikkeling, sociale ontwikkeling, arbeidsmarkt, volkshuisvesting, economie, zorg en welzijn en onderwijs.','Opvraagbaar.',18);
