INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (18,'Resultaten van het model worden altijd geanalyseerd door specialisten.','Ja, opgeven van parameters zoals toekomstige vruchtbaarheid, sterftekansen, immigratie en emigratie, woningvoorraad. De uitkomsten van het model zijn input voor verwerking door diverse beleidsvelden, via menselijke tussenkomst.','Resultaten van het model worden altijd geanalyseerd door specialisten.','Jaarlijks worden de uitkomsten van de prognose vergeleken met de realisatie.',18);
