INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (20, 'Algoritme datakwaliteit','Gemeente Den Haag','Stedelijke Ontwikkeling','Quality-Bot (Q-Bot) ontdekt zelfstandig bestaande en nieuwe patronen in de data. Deze gebruiken we als Quick Scan om de datakwaliteit in te schatten en mogelijke fouten te ontdekken. Vervolgens kunnen goede patronen geadopteerd worden als datakwaliteitsregels om de datakwaliteit continu te monitoren.','Zelflerend','Interne analyse','','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (20,'Doel van het algoritme is om patronen om mogelijke (structurele) fouten in data te ontdekken om deze te vertalen naar kwaliteitsregels. Het is bedoeld om de bronhouder te voeden met nieuwe inzichten over de data kwaliteit. Het is niet bedoeld om zelfstandig data te corrigeren.','Het effect is indirect voor de burger. Het effect is direct voor de medewerkers van Den Haag die ondersteund worden om de kwaliteit van de dienstverlening naar de burger te verbeteren. De kwaliteit van de registratie wordt beter en daarmee ook de kwaliteit van de dienstverlening.','Ja, Q-bot brengt focus aan bij het verbeteren van datakwaliteit. Het helpt dataspecialisten gericht fouten of bijzonderheden in data op te sporen en op te lossen.','Draagt bij aan het data controle en correctie proces. De rol van het algoritme is om suggesties te doen voor verbetering met als doel om de data beter kloppend te maken. Beleid en besluitvorming blijven bij de bronhouder liggen.','n.v.t., Phinion biedt dienst aan als een SaaS.',20);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (20,'De data wordt gestructureerd in views door de technische gebruiker. Vervolgens worden de analyse voorkeuren en instellingen bepaald. Daarna gaat het algoritme autonoom op zoek naar significant sterke patronen in de data. Deze worden verzameld in een lijst. Vervolgens worden deze beoordeeld op kwaliteit en relevantie. De meeste sterke patronen worden geselecteerd. Tenslotte worden de patronen weer toegepast op de originele data en wordt per veld vastgesteld welke normaal (regelmatig) zijn en welke afwijkend (onregelmatig) zijn. Deze worden dan groen of rood aangemerkt.','Proprietary code. Niet publiek beschikbaar.','',True,'Erfpacht contracten, eigendommen en kadasterpercelen. Deze zijn aan elkaar verbonden.','20');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (20,'Risico''s worden beheerst door middel van evaluatie van medewerkers.','Ja, dit is de kern van de applicatie.','Beheersing van de risico''s ligt bij de gemeente. De risico''s zijn beperkt omdat het algoritme geen zelfstandige beslissingen neemt. De bias is uitsluitend technisch van aard en heeft geen impact op de besluitvorming en de burger.','Prestatie gebeurt via gebruikersevaluatie. De medewerkers bepalen welke patronen ze willen gebruiken en hoe goed deze zijn.',20);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (20,'','Geen, er worden geen persoonsgegevens verwerkt.',True,'Er worden geen persoonsgegevens verwerkt.','Ja door de medewerkers erfpacht, en dat is juist de bedoeling.',20);
