INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (21, 'Herkenning bijplaatsing ORAC''s','Gemeente Den Haag','Stadsbeheer','Digitaal scannen en categoriseren van types bijplaatsingen bij Orac’s.','Zelflerend','Infrastructuur','','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (21,'De technische ontwikkeling inzake beeldherkenning uitbaten in het operationeel proces door gebruik te maken van de scanauto’s van parkeerhandhaving. Beeldherkenning kan een goede toevoeging zijn aan de verschillende manieren waarop bijplaatsingen worden gesignaleerd, aangezien scanauto’s nu ook al door de stad rijden. Het doel is het versterken van de MOR-keten en opvolging te versnellen. Reductie op herhaalmeldingen, dubbelmeldingen, handmatige handelingen en de opvolgtijd van opruimen is -naast de dienstverlening aan de burger- een significante verbetering van de bedrijfsvoering voor de ketenpartners. Met toepassing van scantechnologie wordt beoogd om met -uitbreiding van- bestaande middelen de meldingenketen extra te voeden. De opruiming van afval en de handhaving erop kunnen mogelijk nog sneller worden opgevolgd.','• Bijplaatsingen worden sneller gedetecteerd en opgeruimd (schone stad);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (21,'De scanvoertuigen worden voorzien van de exacte locaties (GPS-coördinaten) waar de ORAC’s zich bevinden. Aan de hand daarvan wordt gescand en bepaald wanneer overzichtsfoto’s gemaakt moeten worden. Van elke ORAC worden negen foto’s opgestuurd; 3 van de camera die naar voren is gericht, 3 van de haakse camera en 3 van de camera die naar achteren is gericht. Het maakt daarbij niet uit of de ORAC’s rechts of links van de scanauto binnen het gezichtsveld van de camera’s komen.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (21,'n.v.t.','Een deskforce monitort de beeldherkenning met name om het zelflerend effect van de toegepaste kunstmatige intelligentie te bevorderen op automatische toekenning aan de juiste afvalcategorie en de opvolging van de informatievoorziening naar de betrokken ketenpartners.','Er zijn risico''s m.b.t. opslag beelden, deze zijn nog niet in kaart gebracht.','n.v.t.',21);
