INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (23, 'Smartcity/ geluidsherkenning','Gemeente Den Haag','Bedrijfsvoering','Algoritme dat op basis van spectrogrammen detecteert welk geluid in het spectrogram te zien is. In ontwikkeling, nog niet actief, er wordt nog geen data gedeeld.','Zelflerend','Sociale zekerheid','','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (23,'Het algoritme is ontwikkeld om geluiden te herkennen. In de huidige toepassing wordt voor geluiden die boven een bepaald decibelniveau liggen vastgesteld wat de aard van het luide geluid was. Dit geeft inzicht in het soort geluiden dat overlast geeft. Het algoritme is bedoeld om een globaal beeld te krijgen waar, wanneer er wat voor soorten geluidsoverlast aanwezig zijn zodat daar verder onderzoek naar gedaan kan worden. De uitkomsten van het algoritme zijn niet bedoeld als grond voor directe besluiten of acties zonder verificatie van de uitkomsten.','Het effect van de uitkomst van het algoritme: Door een beter inzicht in de oorzaak van geluidsoverlast die wordt ervaren door burgers, kan de gemeente haar taak in het handhaven van een prettige comfortabele buitenruimte beter uitvoeren. Naast het kunnen vaststellen van het type geluid (geluidherkenning) werd in een testopstelling ook het geluidniveau (decibel) gemeten. Op dit moment zijn er geen burgers die in contact komen met het algoritme. Twee bewoners van de omgeving waar dit algoritme is getest zijn tijdens de duur van de test betrokken geweest om te onderzoeken of de beoogde concept toepassing werkt. Zij hebben middels een dashboard inzicht gekregen in de decibelniveau’s die gemeten worden in de buitenruimte (nb: het algoritme zelf meet geen decibels). Bij deze twee bewoners is een sensor aan hun gevel opgehangen om te kunnen verifiëren wat de geluidsniveau’s bij hun woning zijn. Naast deze twee intensief betrokken bewoners is tijdens de zomer van 2020 een groter evenement georganiseerd om bezoekers en bewoners van en aan de boulevard te informeren, tijdens het Smart @Sea festival. Via publicaties in de (lokale) kranten, op websites van de gemeente Den Haag en Provincie Zuid Holland worden bewoners geïnformeerd over de diverse testen die worden uitgevoerd in het kader van het Living Lab Scheveningen programma. Deze test om inzicht te krijgen in geluidsoverlast en geluidsbeleving valt ook binnen dat programma. https://www.denhaag.nl/nl/in-de-stad/wonen-en-bouwen/ontwikkelingen-in-de-stad/ontwikkelingen-scheveningen-kust/nieuwe-uitvindingen-in-living-lab-scheveningen.htm','Er is geen alternatief voor het geluidsherkenningsalgoritme. Het algoritme is een toevoeging op het meten van geluidsniveau’s.','Het algoritme wordt niet gebruikt voor besluitvorming. Het algoritme bepaalt uitsluitend welk geluid zichtbaar is in een spectrogram.','Niet beschikbaar',23);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (23,'Het algoritme zet geluidsfragmenten van 4 seconden om in spectrogrammen, visuele weergaven van het geluid. De spectrogrammen zijn afbeeldingen van 128x128 pixels met grijswaarden. Deze spectrogrammen worden vervolgens door een beeldherkenningsalgoritme geclassificeerd in een aantal categorieën, zoals "stilstaand verkeer", "muziek", "getoeter", etc. Dit beeldherkenningsmodel is een relatief klein convolutional neural network van 5 lagen. (Drie convolutional lagen gevolgd door max-pooling en twee fully connected lagen.) Het model draait in python, gebruikmakend van de Tensorflow library.','Niet beschikbaar.','',True,'Het algoritme verwerkt uitsluitend spectrogrammen die weer zijn gegenereerd op basis van zelf ingewonnen audiobestanden.','23');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (23,'','Nee','Het algoritme is getraind op openbare datasets van vergelijkbare geluiden. Omdat deze niet altijd hetzelfde klinken en dezelfde karakteristieken hebben (en aard) als de geluiden die op locatie worden opgevangen zou het algoritme fouten kunnen maken in de geluidsherkenning. Omdat het algoritme alleen ter beeldvorming dient levert dit niet direct risico''s op voor de betrokken burgers en ondernemers. Het risico op uitlekken van data (opgenomen geluiden) is ondervangen door het geluid niet op het apparaat op te slaan maar direct de geluidsherkenning op het apparaat uit te voeren.','Er zijn geen afspraken vastgelegd om de kwaliteit van de geluidsherkenning te verifiëren. Het algoritme geeft zelf overigens ook aan met welke mate van zekerheid een geluid is herkend.',23);
INSERT INTO juridisch(id, competent_authority, lawful_basis, dpia, dpia_description, objection_procedure, algoritme_id ) VALUES (23,'','Er is geen wettelijke grondslag gekozen. Zo lang het systeem nog niet werkt, is het ook niet nodig om de DPIA uit 2019 bij te werken.',True,'De gemeente Den Haag is de Verwerkingsverantwoordelijke, Cyrb de verwerker. Er is geen verstrekker of ontvanger.','Er is geen formele bezwaarprocedure. Het algoritme herkent geluiden in de openbare ruimte. Op basis van het algoritme worden geen besluiten genomen waar individuen mee tekort kunnen worden gedaan. Mochten burgers onverhoopt bezwaar willen maken dan kan dit door het bezwaar kenbaar te maken via info@cyrb.nl.',23);
