INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (24, 'Segmentatie analyse eenzaamheid','Gemeente Den Haag','Onderwijs, Cultuur en Welzijn','Segmentatie van bevolking van Den Haag in mate van eenzaamheid op basis van sociaal demografische kenmerken','Rule based','Bevolkingsonderzoek','','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (24,'Het identificeren van kwetsbare groepen in het kader van eenzaamheid en de meest belangrijke oorzaken van het ontstaan van eenzaamheid bij die kwetsbare groepen, om zo het beleid en de uitvoering effectiever te maken.','Als de uitkomst van de analyse wordt gebruikt voor beleid en uitvoering kan dit een verdere focus betekenen op die groepen die ook daadwerkelijk eenzaamheid ervaren. Hierbij zijn individuele personen niet herleidbaar en kunnen dan ook niet op een directe manier worden benaderd.','In eerdere stadia zijn meer kwalitatieve methoden voor hetzelfde doel toegepast (gesprekken met professionals en ervaringsdeskundigen). Het algoritme dient in die zin ook als validatie.','Beleid en uitvoering programma voor opgave eenzaamheid','Alle documenten en onderliggende berekeningen zijn overgedragen aan de opgavemanager eenzaamheid.',24);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (24,'Zie bijgevoegde documententatie','','',True,'Zie bijgevoegd dataoverzicht','24');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (24,'Er zijn een aantal werkwijzen gehanteerd: 1) BSN''s worden omgezet naar een identifier zodat personen niet herleidbaar zijn, 2) Onderzoek is uitgevoerd op een Datalab (beveiligde omgeving waar door middel van dubbele autorisatie ingelogd kan worden en waar geen internetverbinding op beschikbaar is). Deze omgeving (incl. data) is na afloop van het onderzoek verwijderd, 3) Alleen geaggregeerde gegevens worden gepresenteerd in presentaties en dashboard.','Ja, de uitkomsten worden gebruikt als validatie voor kwalitatieve analyses.','Het aantal en type kenmerken die in de analyse zijn meegenomen zijn arbitrair. Er is een zo breed mogelijke set meegenomen, mede op basis van eerdere onderzoeken en eigen regressie analyses. Doordat ook kwalitatieve analyses zijn gedaan, staat het algoritme niet los van de realiteit.','De prestaties van het algoritme worden afgezet ten opzichte van kwalitatieve analyses om zo de kwaliteit te waarborgen.',24);
