INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (32,'Technische ondersteuning voor het verifiëren van de kwaliteit van de foto.','Als de foto niet voldoet aan de vastgestelde kwaliteitscriteria, moet er een nieuwe foto aangeleverd worden door de burger.','De inzet van het algoritme is een kosteneffectieve manier om de kwaliteit van de pasfoto in het identiteitsdocument te borgen. De inzet van het algoritme heeft geen herkenningsdoeleinde, maar zorgt alleen voor de meting van de kwaliteit, om ervoor te zorgen dat kwalitatief zo hoog mogelijk foto’s in het identiteitsdocument worden opgenomen.
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (32,'','','',True,'Voor het trainen, testen en valideren worden afzonderlijke datasets gebruikt. De trainings-, test- en validatiedataset bevatten representatieve gegevens van de wereldbevolking.','32');
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (32,'Het algoritme wordt ingezet als ondersteunend middel in het kwaliteitsbeoordelingsproces. Er vindt geen automatische besluitvorming door het algoritme plaats. De ambtenaar is de eindverantwoordelijk in het accepteren of niet accepteren van de foto in het aanvraagproces en besluit of de foto voldoet aan alle vereiste kwaliteitscriteria.','De ambtenaar heeft de keuze om bij een door het algoritme afgekeurde foto deze alsnog te accepteren. De ambtenaar kan hierbij een of meerdere keuzes selecteren en heeft de volgende opties:
