INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (33, 'Profiel Bewoningsgraad Landelijke Aanpak Adreskwaliteit (LAA)','Ministerie BZK','Rijksdienst voor Identiteitsgegevens (RvIG)','Dit algoritme controleert of de BAG-registratie van bepaalde woonadressen juist is.','Rule based','Basisregistratie','https://www.rvig.nl/over-rvig/algoritmeregister/profiel-bewoningsgraad-landelijke-aanpak-adreskwaliteit-laa','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (33,'Het doel van dit algoritme is om potentiële risicoadressen te selecteren op basis van gerede twijfel en deze aan gemeenten te leveren voor nader adresonderzoek. Op basis van deze adresonderzoeken worden onjuiste adresgegevens gecorrigeerd, zodat overheidsorganen op basis van juiste informatie hun publieke taken kunnen uitvoeren.','Als een onderzoek naar inschrijving van personen op het adres plaats gaat vinden, wordt er huisbezoek op het adres gedaan.','Het efficiënt en zo effectief mogelijk vinden van adressen waar de kans van incorrecte inschrijvingen op adressen zo groot mogelijk is (de trefkans is minimaal 50%) en deze inschrijvingen corrigeren. Met behulp van het algoritme kun je prioriteit geven aan adressen waarbij de kans groter dan 50% is dat er iets niet klopt in de registratie en zorg je er tegelijkertijd voor dat je niet onnodig bij burgers aan de deur komt. Er is bij dit algoritme geen sprake van automatische besluitvorming; er is altijd menselijke tussenkomst door de ambtenaar van de gemeente.','','https://www.rvig.nl/brp/landelijke-aanpak-adreskwaliteit-laa',33);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (33,'De adressen moeten voor het profiel Bewoningsgraad voldoen aan onderstaande criteria:
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (33,'','Er is geen sprake van automatische besluitvorming. De beoordeling en beslissingen worden altijd door mensen gedaan. Algoritmes zijn een middel en zijn niet leidend. Tijdens het ontwikkelen van alle LAA-algoritmen is advies gevraagd aan een representatieve vertegenwoordiging van gemeenten.','De persoonlijke levenssfeer komt mogelijk in het gedrang, door het verwerken van persoonsgegevens en het afleggen van een huisbezoek door gemeenten.','De trefkans is minimaal 50%.',33);
