INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (34, 'Kinderbijslag','Ministerie SZW','Sociale Verzekeringsbank (SVB)','Door algoritmes kunnen we automatisch bepalen of u recht heeft op kinderbijslag.','Rule based','Sociale zekerheid','https://www.svb.nl/nl/over-de-svb/hoe-werken-we/algoritmes-kinderbijslag','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (34,'Met een algoritme is beter te voorspellen hoeveel alle uitkeringen bij elkaar gaan kosten voor de Rijksbegroting. Een goed algoritme zorgt ervoor dat we minder klantdossiers handmatig verwerken. Dit bespaart kosten en onze medewerkers kunnen hun aandacht richten op maatwerk waar dat nodig is.','Als wij de kinderbijslag verkeerd berekenen kunt u in de problemen komen. U krijgt dan te weinig geld om te leven. Of u krijgt teveel en moet dat later terugbetalen. Dit is vervelend en kan leiden tot schulden. Een algoritme verwerkt gegevens vooral automatisch. Daarmee voorkomt het schulden.','','','',34);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (34,'De gemeente levert gegevens aan van het kind en de ouders. Een van de algoritmes combineert al deze gegevens. Hiermee bepalen wij of het nodig is om u een aanvraagformulier voor de kinderbijslag te sturen. Wij sturen geen aanvraagformulier als u geen recht op kinderbijslag heeft.
