INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (36, 'Sancties','Ministerie SZW','Sociale Verzekeringsbank (SVB)','Een beslistool helpt onze medewerkers om te beoordelen of wij een boete of maatregel moeten geven.','Rule based','Sociale zekerheid','https://www.svb.nl/nl/over-de-svb/hoe-werken-we/algoritmes-sancties','In gebruik');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (36,'Algoritmes helpen bij moeilijke beslissingen waarbij verschillende zaken een rol spelen. Hierdoor worden minder fouten gemaakt. Het systeem behandelt iedereen in dezelfde situatie op dezelfde manier.','Een sanctie heeft vaak financiële gevolgen voor u. Daarnaast heeft een sanctie ook emotionele gevolgen. Nog meer dan het terug moeten betalen van een bedrag. Het geven van een sanctie heeft ook gevolgen voor de manier waarop moet worden terugbetaald. Schulden komen vaak voor.','','','',36);
