INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (37,'Als u buiten Nederland gaat wonen, betaalt u geen belasting meer in Nederland. En daardoor stopt de opbouw van uw AOW. U of uw werkgever kunnen de verzekering voor de AOW en Anw door laten gaan. Hiervoor moet een premie betaald worden. Hoe hoog de premie is, hangt af van uw inkomen. Een algoritme berekent de hoogte van die premie.','','',True,'Onze belangrijkste bronnen zijn:
