INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (38, 'Preventie & Handhaving','Ministerie SZW','Sociale Verzekeringsbank (SVB)','Met dit model voorspellen wij hoe groot de kans is dat een alleenstaande toch samenwoont.','Zelflerend','Sociale zekerheid','https://www.svb.nl/nl/over-de-svb/hoe-werken-we/algoritmes-preventie-en-handhaving','In ontwikkeling');
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (38,'Door het SWAN-model kunnen wij ons richten op het verminderen van risico’s. Wij kunnen situaties waarin u krijgt waar u geen recht op heeft eerder herkennen. En aanpassen om het bedrag dat u terug moet betalen laag te houden. Ook zorgen wij ervoor dat toezichthouders nuttig worden ingezet. Het model zorgt ervoor dat we geld besparen op uitkeringen en op het werk dat we uitvoeren.','Om vast te stellen of iemand samenwoont, kan onze afdeling Preventie & Handhaving u thuis bezoeken. Dit zijn persoonlijke bezoeken. Daarom maken we met het SWAN-model een selectie binnen de groep, zodat er zo min mogelijk mensen worden gecontroleerd.','','','',38);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (38,'Iemand die samenwoont krijgt minder AOW dan een alleenstaande. Wij kunnen controleren of een alleenstaande die volgens onze systemen alleen woont, toch niet samenwoont. Dit heet het SWAN-model. Met dit model voorspellen wij hoe groot de kans is dat een alleenstaande toch samenwoont. Het SWAN-model is een algoritme.','','',True,'Onze belangrijkste bronnen zijn interne gegevens die beschikbaar zijn over de klant. Bijvoorbeeld: 
