INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (39, 'Beoordeling AOW-verstrekking','Ministerie SZW','Sociale Verzekeringsbank (SVB)','Ondersteunt bij de beoordeling van
INSERT INTO inzet (id, goal, impact, proportionality, decision_making_process, documentation, algoritme_id) VALUES (39,'Met een algoritme is beter te voorspellen hoeveel alle uitkeringen bij elkaar gaan kosten voor de Rijksbegroting. Een goed algoritme zorgt ervoor dat we minder klantdossiers handmatig verwerken. Dit bespaart kosten en onze medewerkers kunnen hun aandacht richten op maatwerk waar dat nodig is.','Als wij de AOW verkeerd berekenen kunt u in de problemen komen. U krijgt dan te weinig geld om van te leven. Of u krijgt teveel en moet dat later terugbetalen. Dit is vervelend en kan leiden tot schulden. Een algoritme verwerkt gegevens vooral automatisch. Daarmee voorkomt het schulden.','','','',39);
INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (39,'Een van de algoritmes combineert alle gegevens die vooraf bekend en nodig zijn. Zoals woonplaats, verblijf en werk in het buitenland. Met die gegevens berekenen wij of iemand recht heeft op een complete AOW. Als de AOW is toegewezen, zorgen andere algoritmes dat de betaling per maand automatisch gaat. Eenvoudige wijzigingen als een verhuizing van het hele gezin binnen Nederland worden automatisch verwerkt.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (39,'','','We verwerken erg gevoelige gegevens. Daarom zetten wij de meest ervaren medewerkers in bij het ontwikkelen van de algoritmes. Ook gebruiken wij voor deze algoritmes klantpanels om tijdens de ontwikkeling mee te kijken.
