INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (41, 'Vroeg eropaf','Gemeente Amsterdam','Directie Inkomen','In Amsterdam strijden we tegen armoede. Mensen die in armoede dreigen te raken proberen we zo vroeg mogelijk te helpen, om te voorkomen dat ze ernstige schulden krijgen of uit hun woning gezet worden','Rule based','Sociale zekerheid','https://algoritmeregister.amsterdam.nl/vroeg-eropaf/','In gebruik');
