INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (42, 'Handhaving illegale vakantieverhuur','Gemeente Amsterdam','Directie Wonen, Toezicht en Handhaving','Door gegevens te analyseren, schat het algoritme het risico in dat de woning op het gemelde adres illegaal wordt verhuurd.','Zelflerend','Veiligheid en handhaving','https://algoritmeregister.amsterdam.nl/handhaving-illegale-vakantieverhuur/','In gebruik');
