INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (42,'In Amsterdam is er beperkt woon- en verblijfsruimte, zowel voor Amsterdammers als voor bezoekers. Het verhuren van een woning of woonboot aan toeristen moet aan bepaalde voorwaarden voldoen. Het mag bijvoorbeeld maximaal 30 nachten per jaar en aan 4 personen per keer. Het moet gemeld worden bij de gemeente.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (42,'','Er is geen sprake van geautomatiseerde besluitvorming. Als we een adres onderzoeken op het vermoeden van illegale vakantieverhuur komt dit voort uit een melding van bijvoorbeeld een omwonende of een verhuurplatform. Het algoritme helpt de medewerkers van Toezicht & Handhaving te bepalen bij welke meldingen de kans op illegale verhuur het grootst is, zodat die meldingen het eerst onderzocht kunnen worden. De medewerker krijgt een overzicht te zien, die toont op grond van welke criteria het algoritme het risico op illegale vakantieverhuur hoog of laag inschat. Op die manier maken we inzichtelijk waarop het algoritme de risico-inschatting heeft gebaseerd.
