INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (44, 'Parkeercontrole','Gemeente Amsterdam','Parkeren, Team Analyse & Advies','Een beeldherkenningsalgoritme om kentekens te identificeren op camerabeelden.','Zelflerend','Veiligheid en handhaving','https://algoritmeregister.amsterdam.nl/parkeercontrole/','In gebruik');
