INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (44,'Om Amsterdam leefbaar en toegankelijk te houden, mag er maar een beperkt aantal auto’s in de stad parkeren. De gemeente controleert of een geparkeerde auto het recht heeft om geparkeerd te staan, dus of iemand parkeergeld heeft betaald of een parkeervergunning heeft. Om efficiënter te werken doen we die controle met scanauto’s. Daarmee controleren we momenteel meer dan 150.000 officiële parkeerplaatsen in Amsterdam.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (44,'','Menselijke controleurs beoordelen de beelden om te zien of er sprake is van een speciale situatie, bijvoorbeeld laden of lossen, of stilstaande auto’s voor een verkeerslicht. Zij controleren ook of kenteken goed gelezen is. Bij twijfel bekijkt een parkeercontroleur de situatie ter plaatse.','Het algoritme verwerkt geen informatie die kan leiden tot discriminatie. De service werkt op dezelfde manier voor alle kentekenplaten, ongeacht het automodel, de leeftijd van de auto of de kentekenhouder.','',44);
