INSERT INTO algemene_informatie (id, name, organization, department, description_short, type, category, website, status ) VALUES (45, 'Public Eye','Gemeente Amsterdam','CTO Innovatieteam','Met het crowdmonitoring systeem ‘Public Eye’ brengen we op een paar plekken in Amsterdam de drukte in kaart.','Zelflerend','Veiligheid en handhaving','https://algoritmeregister.amsterdam.nl/public-eye/','In gebruik');
