INSERT INTO toepassing (id, description, application_url, publiccode, mprd, source_data, algoritme_id) VALUES (45,'Amsterdam is een drukke stad. Dit kan soms leiden tot verkeersonveilige situaties. Door data te verzamelen over de aantallen voetgangers is het mogelijk om maatregelen te treffen, waardoor de drukte in goede banen geleid kan worden. Hierdoor blijft de stad comfortabel, bereikbaar en verkeersveilig. Als een situatie door te grote drukte onveilig wordt, kan de gemeente ingrijpen. Dit gebeurt bijvoorbeeld door digitale informatieborden te plaatsen, zodat mensen weten welke routes ze moeten nemen. Of er wordt éénrichtingsverkeer ingesteld.
INSERT INTO toezicht (id, monitoring, human_intervention, risks, performance_standard, algoritme_id) VALUES (45,'','Aan de hand van de trainingsdata wordt de kwaliteit en nauwkeurigheid van het algoritme periodiek geëvalueerd door een klein aantal medewerkers van de gemeente die toestemming hebben om de beelden te bekijken. Zij bekijken of het algoritme terecht mensen herkent als mensen.','Het algoritme wordt getraind met allerlei beelden zonder rekening te houden met het uiterlijk van de mensen op die beelden. Het algoritme telt uitsluitend het aantal hoofden.','',45);
